package sd;import java.rmi.*;public class Client{    public static void main(String args[]){	try {	    RemoteCounterI counter = (RemoteCounterI) Naming.lookup("//localhost:1234/myCounter");	    counter.increment(3);	    System.out.println("leu o contador = " + counter.read());	}	catch (RemoteException e) {	    System.out.println(e.getMessage());	}	catch (Exception e) {	    System.out.println(e.getMessage());	}    }}	             